mapper.select:
select 
	* 
from 
	exer_bbs a 
where 
	ex_id=#{ex_id}
and
	ex_comm_idx=#{ex_comm_idx}
and 
	to_char(a.ex_writedate,'yyyymmdd')=#{ex_writedate}

select 
	to_char(ex_writedate,'yyyymmdd')ex_writedate 
from 
	exer_bbs 
where 
	ex_id=#{ex_id}
and 
	ex_comm_idx=#{ex_comm_idx} 
and 
	ex_group=#{ex_group}
